Token($value, PHPExcel_Calculation_FormulaToken::TOKEN_TYPE_OPERAND);                      $value = "";                  }                  $tokens1[] = new PHPExcel_Calculation_FormulaToken($this->formula{$index}, PHPExcel_Calculation_FormulaToken::TOKEN_TYPE_OPERATORPOSTFIX);                  ++$index;                  continue;              }                             if ($this->formula{$index} == PHPExcel_Calculation_FormulaParser::PAREN_OPEN) {                  if (strlen($value) > 0) {                      $tmp = new PHPExcel_Calculation_FormulaToken($value, PHPExcel_Calculation_FormulaToken::TOKEN_TYPE_FUNCTION, PHPExcel_Calculation_FormulaToken::TOKEN_SUBTYPE_START);                      $tokens1[] = $tmp;                      $stack[] = clone $tmp;                      $value = "";                  } else {                      $tmp = new PHPExcel_Calculation_FormulaToken("", PHPExcel_Calculation_FormulaToken::TOKEN_TYPE_SUBEXPRESSION, PHPExcel_Calculation_FormulaToken::TOKEN_SUBTYPE_START);                      $tokens1[] = $tmp;                      $stack[] = clone $tmp;                  }                  ++$index;                  continue;              }                             if ($this->formula{$index} == PHPExcel_Calculation_FormulaParser::COMMA) {                  if (strlen($value) > 0) {                      $tokens1[] = new PHPExcel_Calculation_FormulaToken($value, PHPExcel_Calculation_FormulaToken::TOKEN_TYPE_OPERAND);                      $value = "";                  }                    $tmp = array_pop($stack);                  $tmp->setValue("");                  $tmp->setTokenSubType(PHPExcel_Calculation_FormulaToken::TOKEN_SUBTYPE_STOP);                  $stack[] = $tmp;                    if ($tmp->getTokenType() == PHPExcel_Calculation_FormulaToken::TOKEN_TYPE_FUNCTION) {                      $tokens1[] = new PHPExcel_Calculation_FormulaToken(",", PHPExcel_Calculation_FormulaToken::TOKEN_TYPE_OPERATORINFIX, PHPExcel_Calculation_